interface PropsType {
  notificationInfo: {
    profile: string;
    name: string;
    notification: string;
    boldNotification?: string | null;
    group?: string;
    message?: string;
    timestamp: string;
    isRead: boolean;
  }
}

export default function Card({ notificationInfo }: PropsType) {
  const { name, profile, notification, boldNotification, group, message, timestamp, isRead } = notificationInfo;
  return (
    <div className='notification-card'>
      <section className='notification-card__left-wrap'>
        <img src="/images/avatar-angela-gray.webp" alt="프로필 이미지" />
      </section>
      <section className='notification-card__right-wrap'>
        <div className='notification-card__first-line-wrap'>
          <p className='notification-card__name'>{name}</p>
          <p className='notification-card__notification'>followed you</p>
          <span className='notification-card__group'>Chess Club</span>
          <div className='notification-card__unread-mark'></div>
        </div>
        <p className='notification-card__time'>5m ago</p>
        <div className='notification-card__message-container'>
          <p className="notification-card__message">
            Hello.
          </p>
        </div>
      </section>
    </div>
  )
}
